mapper.select:
	 SELECT * FROM (
	 SELECT rownum rnum, B.* FROM (
	select board_idx, title, content, member.nickname, hit, recommend, writtendate
from board, member
where board.member_idx = member.member_idx

	 ORDER BY board_idx DESC
	 ) B
	 ORDER BY rnum
	 )
	 WHERE rnum BETWEEN #{startNo} AND #{endNo}
