mapper.select:
select * from (
	select rownum rnum, B.* from (
		select * from memo
		where member_idx = #{member_idx} and mark = 1
		order by memo_idx desc
	) B order by rnum
) memo
where rnum between #{startNo} and #{endNo}

select * from (
	select rownum rnum, B.* from (
		select * from memo
		where member_idx = #{member_idx} and mark = 2
		order by memo_idx desc
	) B order by rnum
) memo
where rnum between #{startNo} and #{endNo}
