mapper.select:

    		SELECT * FROM (
                SELECT rownum rnum, B.* FROM (
				SELECT notice_idx, title, content, member_idx, hit, writtendate FROM notice
				ORDER BY notice_idx DESC
		 	)B
			ORDER BY rnum
		) notice
		WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE notice
SET hit = hit+1
WHERE notice_idx = #{notice_idx }
