mapper.select:
SELECT
notice_idx, title, content, hit, writtendate, member.nickname
FROM notice,member WHERE notice.member_idx = member.member_idx AND notice_idx = #{notice_idx }

SELECT fileno, origin_name, stored_name, notice_idx
FROM NoticeFile
WHERE notice_idx = #{notice_idx }

SELECT fileno, origin_name, stored_name, notice_idx
FROM NoticeFile
WHERE fileno = #{fileno }

mapper.update:
UPDATE notice
SET hit = hit+1
WHERE notice_idx = #{notice_idx }

UPDATE notice
SET title = #{title },
	content = #{content }
WHERE notice_idx = #{notice_idx }
