mapper.select:
select calendar_idx,  member_idx, title, content, rdate, wdate
from calendar 
where calendar_idx=#{calendar_idx}

mapper.update:
update calendar 
set title = #{title, jdbcType=VARCHAR}, content =#{content, jdbcType=VARCHAR}
where calendar_idx = #{calendar_idx}

mapper.sql:
select calendar_idx,  member_idx, title, content, rdate, wdate
from calendar
where member_idx = #{member_idx}

mapper.select:
select * from (
	select rownum rnum, B.* from (
		select calendar_idx,  member_idx, title, content, rdate, wdate
		from calendar
		where member_idx = #{member_idx}
			order by calendar_idx desc
				)B
		order by rnum
) R
where rnum between #{startNo} and #{endNo}

SELECT * FROM (

) R
WHERE rnum BETWEEN #{startNo } AND #{endNo }
